"use strict";

var _interopRequireDefault = require("@babel/runtime/helpers/interopRequireDefault");

var _types = require("@polkadot/types");

var _ = _interopRequireDefault(require("."));

// Copyright 2017-2020 @polkadot/rpc-core authors & contributors
// This software may be modified and distributed under the terms
// of the Apache-2.0 license. See the LICENSE file for details.
describe('methodSend', () => {
  const registry = new _types.TypeRegistry();
  let rpc;
  let methods;
  let provider;
  beforeEach(() => {
    methods = {
      blah: {
        method: 'blah',
        params: [{
          name: 'foo',
          type: 'Bytes'
        }],
        section: 'test',
        type: 'Bytes'
      },
      bleh: {
        method: 'bleh',
        params: [],
        section: 'test',
        type: 'Bytes'
      }
    };
    provider = {
      send: jest.fn((method, params) => {
        return Promise.resolve(params[0]);
      })
    };
    rpc = new _.default(registry, provider);
  });
  it('wraps errors with the call signature', done => {
    // private access
    const method = rpc.createMethodSend(methods.blah);
    method().subscribe(() => undefined, error => {
      expect(error.message).toMatch(/blah\(foo: Bytes\): Bytes/);
      done();
    });
  });
  it('checks for mismatched parameters', done => {
    // private method
    const method = rpc.createMethodSend(methods.bleh);
    method(1).subscribe(() => undefined, error => {
      expect(error.message).toMatch(/parameters, 1 found instead/);
      done();
    });
  });
  it('calls the provider with the correct parameters', done => {
    // private method
    const method = rpc.createMethodSend(methods.blah); // Args are length-prefixed, because it's a Bytes

    method(new Uint8Array([2 << 2, 0x12, 0x34])).subscribe(() => {
      expect(provider.send).toHaveBeenCalledWith('test_blah', ['0x1234']);
      done();
    });
  });
});