module.exports = download_range

async function download_range (feed, range) {
  return new Promise((resolve, reject) => {
    if (range) {
      feed.download(range, (err) => {
        if (err) {
          console.log({ text: 'error downloading range', err })
          return reject(err)
        }
        resolve()
      })
    } else {
      feed.download((err) => {
        if (err) {
          console.log({ text: 'error downloading full feed', err })
          return reject(err)
        }
        resolve()
      })      
    }
  })
}