module.exports = download_range

async function download_range (feed, range) {
  return new Promise(async (resolve, reject) => {
    const request = { start: range[0], end: range[1]+1} 
    console.log({ text: 'download range', range, request })
    const r = await feed.download(request)
    await r.downloaded()
    resolve()
  })
}