module.exports = download_range

async function download_range ({ feed, range, signal }) {
  return new Promise(async (resolve, reject) => {
    try {
      signal.addEventListener("abort", () => { reject(signal.reason) })
      const request = { start: range[0], end: range[1]+1} 
      const r = await feed.download(request)
      await r.downloaded()
      resolve()
    } catch(err) {
      abort()
    }
  })
}