module.exports = get_max_index

function get_max_index (ranges) {
  const indexes = []
  for (const range of ranges) {
    for (let index = range[0], len = range[1] + 1; index < len; index++) {
      indexes.push(index)
    }
  }
  var max = indexes.reduce(function(a, b) { return Math.max(a, b) })
  return max
}