const varint = require('varint')

module.exports = serialize_before_compress

function serialize_before_compress (data, unique_el) {
  const dataBuf = Buffer.from(data, 'binary')
  const elBuf = Buffer.from(unique_el, 'binary')
  const len = dataBuf.length + elBuf.length
  const to_compress = Buffer.concat([elBuf, dataBuf], len)
  return to_compress
}